import React from 'react';
import PropTypes from 'prop-types';

class App extends React.Component {

  state = {
    count: 0
  };

  render() { //리액트는 자동으로 클래스 컴포넌트의 render 함수를 실행한다.
    return (
    <div>
      <h1>The number is {this.state.count}</h1>
      <button>Add</button>
      <button>Minus</button>
    </div>

    )
  }
}

export default App;
