import React, { Component } from 'react';

class Item extends Component {
  constructor(props) {
    super(props);
    this.state = {
      selectedProduct: {}
    };
  };

  addToCart(image, name, price, id, quantity, checked) {
    this.setState({
      selectedProduct: {
        image, name, price, id, quantity, checked
      }
    }, () => {
      this.props.addToCart(this.state.selectedProduct)
    })
  }
}

export default Item;
